import React,{useState,useEffect} from 'react';
import './style.css';
import api from '../../services/api';

function User(){
    const [users,setUsers] = useState([]);
    useEffect(()=>{
        api.get('users').then(response => {
            setUsers(response.data);
        })
    },[])
    console.log(users);
    return(
        <div id="user-container">
            <h1>Lista de Usuários</h1>
            <Link className="button" id="create" to={"/profile"} type="button">CRIAR NOVO USUÁRIO</Link>
            <ul className="user-list">
                {users.map(user=>(
                    <li key={user.id}>
                        <strong>Nome:</strong>
                        <p>{user.name}</p>
                        <strong>Email:</strong>
                        <p>{user.email}</p>
                        <strong>Idade:</strong>
                        <p>{user.idade}</p>
                        <strong>Empresa:</strong>
                        <p>{user.empresa}</p>
                        <strong>Banco:</strong>
                        <p>{user.banco}</p>
                        <strong>Agência:</strong>
                        <p>{user.agencia}</p>
                        <strong>Saldo:</strong>
                        <p>{user.saldo}</p>
                        <div className="actions">
                            <button className="button" type="button">Deletar</button>
                            <button className="button" type="button">Acessar</button>
                        </div>
                    </li>
                ))};
            </ul>
            
        </div>
    );

}
export default User;
